class Solution:
    def longestPalindrome(self, s: str) -> str:
        list_chk =[]
        for i in range(len(s)):
            val = ""
            reverse_val =""
            for ch in s[i:]:
                val += ch
                reverse_val = val[::-1]
                if val == reverse_val:
                    list_chk.append(val)
        list_chk.sort(key=len,reverse=1)
        return list_chk[0]

