class Solution:
    def longestPalindrome(self, s: str) -> str:
        list_chk =[]
        for i in range(len(s)):
            val = ""
            reverse_val =""
            elm_chk = ""
            for ch in s[i:]:
                val += ch
                reverse_val = val[::-1]
                if val == reverse_val and len(val) > len(elm_chk):
                    elm_chk = val
            list_chk.append(elm_chk)
            if len(elm_chk) == len(s[i:]):
                break
        list_chk.sort(key=len,reverse=1)
        return list_chk[0]

